import React, { useEffect, useState } from "react";
import axios from "axios";

const Tl = () => {
    const [nhansus, setNhansus] = useState([])
    const [chucvus, setChucvus] = useState([])
    const [chucvu, setChucvu] = useState({
        _id: '',
        Tenchucvu: '',
        Ghichu: '',
        Quyenhang: ''
    })
    useEffect(() => {
        axios.get('http://localhost:3000/nhansu')
            .then(Response => { if (Response.data) { setNhansus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))
        axios.get('http://localhost:3000/chucvu')
            .then(Response => { if (Response.data) { setChucvus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))

    }, []);
    return (
        <div>
            <h1>TÍNH LƯƠNG</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Mã Nhân Viên</th>
                        <th>Tên</th>
                        <th>Chức Vụ</th>
                        <th>Tùy Chọn</th>

                    </tr>
                </thead>
                <tbody>
                    {
                        nhansus.map(e => (
                            <tr key={e._id}>
                                <td>{e.Hoten}</td>
                                <td>{e.Mnv}</td>
                                {/* Find the corresponding job position */}
                                {
                                    chucvus.find(c => c._id === e.Chucvu) ? (
                                        <td>{chucvus.find(c => c._id === e.Chucvu).Tenchucvu}</td>
                                    ) : (
                                        <td>Chưa Có Chức Vụ</td> // Display a message if job position not found
                                    )
                                }
                                <td className='td-tuychon'>
                                    <button className=''>Tính Lương</button>
                                    <button className='bt-chamlai'>Tính Lại</button>
                                </td>
                            </tr>
                        ))
                    }
                        {
                            loc.map(e => {
                                const correspondingChucvu = Chucvu.find((c) => c._id === e.Chucvu || Chucvu.some(item => item._id !== e.Chucvu));
                                if (correspondingChucvu && +correspondingChucvu.Quyenhang > +UQuyenhang) {
                                    if (Chucvu.some(item => item._id === e.Chucvu)) {
                                        return {
                                            id: e._id,
                                            Hoten: e.Hoten,
                                            Cccd: e.Cccd,
                                            Mnv: e.Mnv,
                                            Sdt: e.Sdt,
                                            luong: e.luong,
                                            Tenchucvu: correspondingChucvu.Tenchucvu,
                                            Quyenhang: correspondingChucvu.Quyenhang,

                                        };
                                    }
                                    else {
                                        return {
                                            id: e._id,
                                            Hoten: e.Hoten,
                                            Cccd: e.Cccd,
                                            Mnv: e.Mnv,
                                            Sdt: e.Sdt,
                                            luong: e.luong,
                                            Tenchucvu: 'không có',
                                            Quyenhang: 'không'
                                        };
                                    }
                                } else {
                                    return null;
                                }
                            })
                                .filter(Boolean) // Lọc bỏ các phần tử null
                                .sort((a, b) => a.Quyenhang - b.Quyenhang) // Sắp xếp theo Quyenhang tăng dần
                                .map((e) => (
                                    <tr key={e.id}>
                                        <td>{e.Hoten}</td>
                                        <td>{e.Cccd}</td>
                                        <td>{e.Mnv}</td>
                                        <td>{e.Sdt}</td>
                                        <td>{e.luong}</td>
                                        <td>{e.Tenchucvu ? e.Tenchucvu : '-'}</td>
                                        <td>{e.Quyenhang ? e.Quyenhang : '-'}</td>
                                        <td className='td-tuychon'>
                                            <Link to={'/quanlythongtinnhanvien/Chinhsua/&idns=' + e.id} className='btn btn-info btn-sm me-2'> Chinh sua </Link>
                                            <button className='all-bt-delete' onClick={() => handleDelete(e.id)}> Xoa</button>
                                        </td>
                                    </tr>
                                ))
                        }

                </tbody>
            </table>
        </div>
    );
}

export default Tl;
