import React, { useEffect, useState } from "react";
import axios from "axios";

const Tl = () => {
    const [nhansus, setNhansus] = useState([])
    const [chucvus, setChucvus] = useState([])

    useEffect(() => {
        axios.get('http://localhost:3000/nhansu')
            .then(Response => { if (Response.data) { setNhansus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))
        axios.get('http://localhost:3000/chucvu')
            .then(Response => { if (Response.data) { setChucvus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))

    }, []);
    const filteredChucvu = chucvus.filter(e => {
        const searchTermLower = searchTerm.toLowerCase();
        let dayly = e.Ngay + '/' + e.Thang + '/' + e.Nam;
    
        if (timtheo === "Tencalam") {
            return e.Tencalam.toLowerCase().includes(searchTermLower);
        } else if (timtheo === "Starttime") {
            return e.Starttime.toLowerCase().includes(searchTermLower);
        } else if (timtheo === "Endtime") {
            return e.Endtime.toLowerCase().includes(searchTermLower);
        } else if (timtheo === "Days") {
            return dayly.includes(searchTermLower);
        }
        else{
            return (
                e.Tencalam.toLowerCase().includes(searchTermLower) ||
                e.Ngay.toString().includes(searchTermLower) ||
                e.Thang.toString().includes(searchTermLower) ||
                e.Nam.toString().includes(searchTermLower)
            );
        }
    });
    return (
        <div>
            <h1>TÍNH LƯƠNG</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Mã Nhân Viên</th>
                        <th>Tên</th>
                        <th>Chức Vụ</th>
                        <th>Tùy Chọn</th>

                    </tr>
                </thead>
                <tbody>
                    {
                        nhansus.map(e => (
                            <tr key={e._id}>
                                <td>{e.Mnv}</td>
                                <td>{e.Hoten}</td>
                                <td>{e.Chucvu}</td>
                                <td className='td-tuychon'>
                                    <button className=''>Tính Lương</button>
                                    <button className='bt-chamlai'>Tính Lại</button>
                                </td>
                            </tr>
                        ))
                    }

                </tbody>
            </table>
        </div>
    );
}

export default Tl;
