import React, { useEffect, useState } from "react";
import axios from "axios";

const Tl = () => {
    const [nhansus, setNhansus] = useState([])
    const [chucvus, setChucvus] = useState([])
    const [chucvu, setChucvu] = useState({
        _id: '',
        Tenchucvu: '',
        Ghichu: '',
        Quyenhang: ''
    })
    useEffect(() => {
        axios.get('http://localhost:3000/nhansu')
            .then(Response => { if (Response.data) { setNhansus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))
        axios.get('http://localhost:3000/chucvu')
            .then(Response => { if (Response.data) { setChucvus(Response.data); } else { alert(Response.data) } }).catch(err => console.log(err))

    }, []);
    function laychuvu(a) {
        const filteredChucvu = chucvus.find(e => e.Chucvu === a);
        if (filteredChucvu) {
            return filteredChucvu.Tenchucvu;
        }
        return ''; // Return an empty string if no matching job position is found
    }

    return (
        <div>
            <h1>TÍNH LƯƠNG</h1>
            <table className='table'>
                <thead>
                    <tr>
                        <th>Mã Nhân Viên</th>
                        <th>Tên</th>
                        <th>Chức Vụ</th>
                        <th>Tùy Chọn</th>

                    </tr>
                </thead>
                <tbody>
                    {
                        nhansus.map(e => (
                            <tr key={e._id}>
                                <td>{e.Hoten}</td>
                                <td>{e.Mnv}</td>
                                {/* Find the corresponding job position */}
                                {
                                    chucvus.find(c => c._id === e.Chucvu) ? (
                                        <td>{chucvus.find(c => c._id === e.Chucvu).Tenchucvu}</td>
                                    ) : (
                                        <td>No Job Position</td> // Display a message if job position not found
                                    )
                                }
                                <td className='td-tuychon'>
                                    <button className=''>Tính Lương</button>
                                    <button className='bt-chamlai'>Tính Lại</button>
                                </td>
                            </tr>
                        ))
                    }


                </tbody>
            </table>
        </div>
    );
}

export default Tl;
